import { useRouter } from 'next/router'
//import Markdown from 'react-markdown'
import Layout from '../../components/MyLayout.js'
import getTodos from '../../components/getTodos.js'

export default () => {
  const router = useRouter()
  const todo = getTodos().filter(item => {
    if (item.id === router.query.id) return true;
  });

  // 以下の title の部分は書き換えが必要（追加したやつがErrorになる）
  return (
    <Layout>
      <h1>{router.query.id}</h1>
      <h2>{todo[0].title}</h2>
      <h3>{todo[0].deadline}</h3>


      <style jsx global>{`
        .markdown {
          font-family: 'Arial';
        }

        .markdown a {
          text-decoration: none;
          color: blue;
        }

        .markdown a:hover {
          opacity: 0.6;
        }

        .markdown h3 {
          margin: 0;
          padding: 0;
          text-transform: uppercase;
        }
      `}</style>
    </Layout>
  )
}
